import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

const App = () => {
  const [pokemonData, setPokemonData] = useState(null);
  const [pokemonName, setPokemonName] = useState(''); // Default Pokémon
  const [errorMessage, setErrorMessage] = useState('');
  const [pokemonButtons, setPokemonButtons] = useState(['pikachu', 'charmander', 'bulbasaur']); // Default buttons

  useEffect(() => {
    fetchPokemonData('pikachu'); // Fetch data for the default Pokémon
  }, []);

  const fetchPokemonData = async (name) => {
    try {
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name.toLowerCase()}`);
      setPokemonData(response.data);
      setErrorMessage('');
    } catch (error) {
      setPokemonData(null);
      setErrorMessage('Pokémon not found!');
    }
  };

  const handleButtonClick = (name) => {
    setPokemonName(name);
    fetchPokemonData(name);
  };

  const handleInputChange = (event) => {
    setPokemonName(event.target.value);
  };

  const handleAddButtonClick = async () => {
    if (pokemonName.trim() !== '' && !pokemonButtons.includes(pokemonName.toLowerCase())) {
      const newName = pokemonName.toLowerCase();
      try {
        await fetchPokemonData(newName);
        setPokemonButtons([...pokemonButtons, newName]);
        setPokemonName(''); // Clear the input field after adding the new Pokémon
      } catch (error) {
        setErrorMessage('Pokémon not found!');
      }
    }
  };

  // Prepare data for the bar chart
  const chartData = {
    labels: ['HP', 'Attack', 'Defense', 'Special Attack', 'Special Defense', 'Speed'],
    datasets: [
      {
        label: 'Base Stats',
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        hoverBackgroundColor: 'rgba(75,192,192,0.4)',
        hoverBorderColor: 'rgba(75,192,192,1)',
        data: pokemonData ? pokemonData.stats.map((stat) => stat.base_stat) : []
      }
    ]
  };

  // Chart options
  const chartOptions = {
    scales: {
      yAxes: [{
        ticks: {
          beginAtZero: true
        }
      }]
    }
  };

  return (
    <div>
      <h1>Pokémon Information</h1>
      <input type="text" value={pokemonName} onChange={handleInputChange} />
      <button onClick={handleAddButtonClick}>Add</button>
      {pokemonButtons.map((name) => (
        <button key={name} onClick={() => handleButtonClick(name)}>{name}</button>
      ))}

      {errorMessage && <p>{errorMessage}</p>}

      {pokemonData && (
        <div>
          <h2>{pokemonData.name}</h2>
          <img src={pokemonData.sprites.front_default} alt={pokemonData.name} />
          <p>Type: {pokemonData.types.map((type) => type.type.name).join(', ')}</p>
          <p>Abilities:</p>
          <ul>
            {pokemonData.abilities.map((ability, index) => (
              <li key={index}>{ability.ability.name}</li>
            ))}
          </ul>
          <p>Height: {pokemonData.height}</p>
          <p>Weight: {pokemonData.weight}</p>
          <p>Base Experience: {pokemonData.base_experience}</p>
          <Bar data={chartData} />
        </div>
      )}
    </div>
  );
};

export default App;
